const express = require('express');
const mysql = require('mysql2');
const cors = require('cors');

const app = express();
const port = 3000;

app.use(cors());
app.use(express.json()); // Permite que o servidor entenda JSON no corpo das requisições

// Configuração da conexão com o MySQL
const db = mysql.createConnection({
  host: 'localhost',
  user: 'root', // Substitua pelo seu usuário do MySQL
  password: 'nova_senha', // Substitua pela sua senha do MySQL
  database: 'jogo_da_vida' // Substitua pelo nome do seu banco de dados
});

// Testando a conexão com o banco de dados
db.connect(err => {
  if (err) {
    console.error('Erro ao conectar ao banco de dados:', err);
    return;
  }
  console.log('Conectado ao banco de dados MySQL!');
});

// Criando uma rota de teste
app.get('/', (req, res) => {
  res.send('Servidor rodando corretamente!');
});

// Criando uma rota para buscar missões
app.get('/missoes', (req, res) => {
  db.query('SELECT * FROM missoes', (err, result) => {
    if (err) {
      return res.status(500).json({ error: 'Erro ao buscar missões' });
    }
    res.json(result);
  });
});

// Iniciando o servidor
app.listen(port, () => {
  console.log(`Servidor rodando na porta ${port}`);
});
